import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
@Component({
  selector: 'app-innermembeership2',
  templateUrl: './innermembeership2.component.html',
  styleUrls: ['./innermembeership2.component.css']
})
export class Innermembeership2Component implements OnInit {
title="Please join us!";
title2="Become a member";


onSubmit(data:any) {
  console.warn(data);

if(data==!onsubmit ){
  alert("successful registration ");
}
else{
  alert("Please enter the data ");
}

  }
  constructor(private router: Router) { }

  ngOnInit() {
  }
  goToVotes($myParam: string = ''): void {
    const navigationDetails: string[] = [''];
    if($myParam.length) {
      navigationDetails.push($myParam);
    }
    this.router.navigate(navigationDetails);
  }
 
 
// call this inbuilt method to reset the form
  

}
<h4 class="mb-4 pb-lg-2">{{ title }}</h4>

<form
  href="#"
  #userForm="ngForm"
  (ngSubmit)="onSubmit(userForm.value)"
  method="post"
  class="custom-form membership-form shadow-lg"
  role="form"
>
  <h4 class="text-white mb-4">Become a member</h4>

  <div class="form-floating">
    <input
      type="text"
      name="full-name"
      ngModel
      id="full-name"
      class="form-control"
      placeholder="Full Name"
      required=""
    />

    <label for="floatingInput">Full Name</label>
  </div>

  <div class="form-floating">
    <input
      ngModel
      type="email"
      name="email"
      id="email"
      pattern="[^ @]*@[^ @]*"
      class="form-control"
      placeholder="Email address"
      required=""
    />

    <label for="floatingInput">Email address</label>
  </div>

  <div class="form-floating">
    <textarea
      class="form-control"
      id="message"
      name="message"
      ngModel
      placeholder="Describe message here"
    ></textarea>

    <label for="floatingTextarea"> Comments</label>
  </div>

  <button (click)="goToVotes()" type="submit" class="form-control">
    Submit
  </button>
</form>
